    (gen_random_uuid(), 'Orange sky is gonna grow for sure, but just be careful that it doesn''t lose sight of the individuality of the volunteers.', NULL, 'Innovation', NOW()),
    (gen_random_uuid(), 'It''s all by your attitude pretty much.', NULL, 'Human Connection', NOW()),
